import React, { useEffect } from "react";
import TimeSlot from "./TimeSlot";

const TimeSlotList = ({ timeList, meetingDateList, setMeetingDateList }) => {
  const isSameDateAndTime = (date1, date2) => {
    return date1.getTime() === date2.getTime();
  }
  const handleClickSlot = (e) => {
    const date = e.target.value; // new Date(e.target.time)
    const isPos = e.target.dataset.ispossible;
    console.log('click! before MeetingList : ', meetingDateList);
    console.log('isPos: ', isPos);
    if (isPos) {
      setMeetingDateList((prev) => {
        const meetList = [...prev]
        for(let i = 0; i < meetList.length; i++) {
          if(isSameDateAndTime(meetList[i], date)) {
            const newMeetList = meetList.splice(i, 1);
            return newMeetList;
          }
        }
      });
    } else {setMeetingDateList((prev) => {
      console.log('prev: ', prev);
      return [date, ...prev].sort()
    })}
  };
  useEffect(()=>console.log('meetingDateList after click: ',meetingDateList))

  return (
    <>
      {timeList.map((slot, index) => (<TimeSlot onClick={handleClickSlot} key={index} time={slot.time} isPossible={slot.isPossible} />))}
    </>
  );
};

export default TimeSlotList;
