import React from "react";
import styled from "styled-components";

const TimeSlot = ({onClick, index, time, isPossibleTime}) => {
    if (isPossibleTime) {
        return (
            <button style={{color: 'blue'}} onClick={onClick} key={index} value={time} data-ispossible={isPossibleTime}>
                <p>{time.getMonth()}/{time.getDate()} ({time.getDay("en-US", {weekday: "long"})})</p>
                <p>{time.getHours()}시 {time.getMinutes()}분</p>
            </button>
        );
    } else {
        return (
            <button style={{color: 'black'}} onClick={onClick} key={index} value={time} data-ispossible={isPossibleTime}>
                <p>{time.getMonth()}월 {time.getDate()}일 ({time.getDay()})</p>
                <p>{time.getHours()}시 {time.getMinutes()}분</p>
            </button>
        );
    }

}

export default TimeSlot;
