import React from "react";
import styled from "styled-components";

const StyledButton = styled.button`
    display: flex;
    width: auto;
    height: auto;
    flex: 1 1 20%;
`

const TimeSlot = ({onClick, index, time, isPossibleTime}) => {
    const weekday = ['일', '월', '화', '수', '목', '금', '토'];
    // const month = time.getMonth()+1;
    // const date = time.getDate();
    // const day = weekday[time.getDay()];
    // const hour = time.getHours(); // integer
    // const min = time.getMinutes(); // integer
    if (isPossibleTime) {
        return (
            <StyledButton style={{color: 'blue'}} onClick={onClick} key={index} value={time} data-ispossible={isPossibleTime}>
                <p>{time.getMonth()+1}/{time.getDate()} ({weekday[time.getDay()]})</p>
                <p>{time.getHours().toString()} : {time.getMinutes().toString().padStart(2, '0')}</p>
            </StyledButton>
        );
    } else {
        return (
            <StyledButton style={{color: 'black'}} onClick={onClick} key={index} value={time} data-ispossible={isPossibleTime}>
                <p>{time.getMonth()+1}/{time.getDate()} ({weekday[time.getDay()]})</p>
                <p>{time.getHours().toString()} : {time.getMinutes().toString().padStart(2, '0')}</p>
            </StyledButton>
        );
    }

}

export default TimeSlot;
