import React, { useState, useMemo } from "react";
import { useLocation } from "react-router-dom";
import Container from "../components/Container";
import Calendar from 'react-calendar';
import TimeSlotList from "../components/TimeSlotList";

const CalendarForm = () => {
  const location = useLocation();
  const state = location.state; // state = {meet_name: ~ , ...}

  const [selectedDay, setSelectedDay] = useState(new Date()); // 현재 클릭한 날짜 (Date 객체)
  const [meetingDateList, setMeetingDateList] = useState([]); // 현재까지 저장된 미팅 가능 날짜 객체들
  const timeList = useMemo(()=>renderTimeList(selectedDay, meetingDateList), [selectedDay, meetingDateList]); // TimeSlotList => {time, isPossible}의 List

  const handleClickDay = (day) => {
    setSelectedDay(day);
  }
  const renderTimeList = (day, meetList) => {
    // meetingDateList의 요소들 중, day와 일수가 같은 date object들의 list 반환 -> timeList
    const runningTime = state.runningTime;
    const numSlots = 960 / runningTime;
    let SlotList = [];
    for (let i = 0; i < numSlots; i++) {
      let d = new Date(day);
      d.setMinutes(d.getMinutes() + 360 + runningTime * i);
      console.log(meetList[i]);
      if (meetList.includes(d)) {
        SlotList.push({index:i, time:d, isPossible: true});
      } else {
        SlotList.push({index:i, time:d, isPossible: false});
      }
    }
    setTimeList(SlotList);
  }


  useLayoutEffect(()=>{

  }, [selectedDay, meetingDateList])

  return (
  <Container title='미팅 가능 날짜 선택'>
    <div>
      <p>미팅 정보</p>
      <div>
        <p>미팅 이름: {state.meet_name}</p>
        <p>참여 인원: {state.numJoiner}</p>
        <p>미팅 시간: {state.runningTime}분</p>
      </div>
    </div>
    <Calendar onChange={handleClickDay}/>
    <TimeSlotList timeList={timeList} meetingDateList={meetingDateList} setMeetingDateList={setMeetingDateList} />
  </Container>
  );
};

export default CalendarForm;
