import React, { useState, useEffect, useLayoutEffect } from "react";
import { useLocation } from "react-router-dom";
import Container from "../components/Container";
import Calendar from 'react-calendar';
import SelectTime from "../components/SelectTime";

const CalendarForm = () => {
  const location = useLocation();
  const state = location.state; // state = {meet_name: ~ , ...}

  const [selectedDay, setSelectedDay] = useState(new Date()); // 현재 클릭한 날짜 (Date 객체)
  // const [meetingDateList, setMeetingDateList] = useState([]); // 현재까지 저장된 미팅 가능 날짜 객체들
  const [timeList, setTimeList] = useState([]); // 현재 클릭한 날짜의 클릭 가능한 timeList (Date List)

  const handleClickDay = (day) => {
    setSelectedDay(day);
  }

  const renderSelectTime = (day) => {
    // meetingDateList의 요소들 중, day와 일수가 같은 date object들의 list 반환 -> timeList
    const runningTime = state.runningTime;
    setTimeList(prev => [day, ...prev]);
  }

  useLayoutEffect(()=>{
    // return 뒤에 실행됨 (render 이후, paint 이전)
    console.log(`useLayoutEffect: selectedDay는 ${selectedDay}`)
    // renderSelectTime(selectedDay);
    setTimeList(prev => [selectedDay, ...prev]);
  },[selectedDay]);

  useEffect(()=>{
    console.log(`useEffect: rendering`);
  });

  return (
  <Container title='미팅 가능 날짜 선택'>
    <div>
      <p>미팅 정보</p>
      <div>
        <p>미팅 이름: {state.meet_name}</p>
        <p>참여 인원: {state.numJoiner}</p>
        <p>미팅 시간: {state.runningTime}분</p>
      </div>
    </div>
    <Calendar onChange={handleClickDay}/>
    <SelectTime />
    {/* timeList={timeList} */}
    {console.log(`return`)}
  </Container>
  );
};

export default CalendarForm;
