import React from "react";
import {PossibleSlot, ImpossibleSlot} from "./TimeSlot";

const TimeSlotList = ({ timeList, meetingDateList, setMeetingDateList }) => {
  const isSameDateAndTime = (date1, date2) => {
    return date1.getTime() === date2.getTime();
  }
  const handleClickPossible = (e) => {
    const date = e.target.time; // new Date(e.target.time)
    const copyMeetingList = [...meetingDateList];
    for(let i = 0; i < copyMeetingList.length; i++) {
      if(isSameDateAndTime(copyMeetingList[i].time, date)) {
        const meetList = copyMeetingList.splice(i, 1);
        setMeetingDateList(meetList);
        return;
      }
    }
  };
  const handleClickImpossible = (e) => {
    const date = e.target.value; // new Date(e.target.time)
    // const copyMeetingList = [...meetingDateList];
    // const meetList = copyMeetingList.push(date);
    // meetList.sort();
    console.log(meetingDateList)
    setMeetingDateList((prev) => {
      prev.push(date)
      prev.sort()
      return prev;
    });
    return;
  };

  return (
    <>
      {timeList.map((slot, index) => {
        if (slot.isPossible) {
          return <PossibleSlot onClick={handleClickPossible} key={index} time={slot.time} />;
        } else { return <ImpossibleSlot onClick={handleClickImpossible} key={index} time={slot.time} />; }
      })}
    </>
  );
};

export default TimeSlotList;
